import React, {useEffect, useState} from 'react';
import {TextField, Grid, InputLabel,MenuItem,FormControl,Select} from '@mui/material';
import Button from 'react-bootstrap/Button';
import {Container, Row, Col} from 'react-bootstrap';
import Popup from 'reactjs-popup';
function InputForm(){
    const [inputValues, setInputValues]= useState({
        age:20,
        sex:1,
        trestbps:145,
        fbs:1,
        restecg:1,
        thal:1,
        slope:0,
        cp:3,
        oldpeak:2.3,
        exang:1,
        feature1:2.4,
        ca:5,
        thalach:2,
        chol:200,
      });
      const[respon,setRespon]=useState('op')
      require('react-dom');
      window.React2 = require('react');
      console.log(window.React1 === window.React2);
      const[isLoading,setLoading]=useState(false)
      const[result,setResult]=useState('Hi')


      const handleChange=(event)=>{
        const {name,value}=event.target;
        setInputValues((prevState)=>{
          return{
            ...prevState,
            [name]:parseFloat(value),
          };
        });

        }



      const handleSubmit=(event)=>{

        setRespon('po');
        const userData=inputValues;
        console.log(userData);
        setLoading(true);
        fetch('https://heart-disease-p.herokuapp.com/prediction/',
        {
          headers:{

            'Accept':'application/json',
            'Content-Type':'application/json'
          },
          method:'POST',
          body:JSON.stringify(userData)
        })
        .then(response=>response.json())
        .then(response=>{
          console.log('Got response',response)
          setRespon(response)
          setResult(response.result)

        });
        event.preventDefault();
        setLoading(false);
      };



      return (
        <div className="Form">
         <Grid
         container
         spacing={0}
         direction='column'
         alignItems="center"
         justify="center"
         style={{minHeight:'100vh',minWidth:'100wh'}}>




            <form className='input-form' onSubmit={handleSubmit}> 
            <TextField onChange={handleChange} value={inputValues.age} required  label='Age' variant='standard' />
            <FormControl variant='standard'>
              <InputLabel>Sex</InputLabel>
              <Select
                value={inputValues.sex}
                onChange={handleChange}
                label='Sex'
              >
                <MenuItem value={0}>Female</MenuItem>
                <MenuItem value={1}>Male</MenuItem>

              </Select>

            </FormControl>
            <FormControl variant='standard'>
              <InputLabel>Chest Pain Type</InputLabel>
              <Select
                value={inputValues.cp}
                onChange={handleChange}
                label='Chest Pain Type'
              >
                <MenuItem value={0}>0</MenuItem>
                <MenuItem value={1}>1</MenuItem>
                <MenuItem value={2}>2</MenuItem>
                <MenuItem value={3}>3</MenuItem>
              </Select>
            </FormControl>
            <TextField required value={inputValues.chol} label='Serum Cholestrol Amount' variant='standard' onChange={handleChange} />
            <FormControl>
              <InputLabel>Resting Blood Pressure</InputLabel>
              <Select
                value={inputValues.trestbps}
                onChange={handleChange}
                label='Resting Blood Sugar'
              >
                <MenuItem value={0}>0</MenuItem>
                <MenuItem value={1}>1</MenuItem>
              </Select>
            </FormControl>


            <Popup trigger={<Button variant="primary" type="submit">
              Submit
            </Button>}
            modal
            nested>
              <p>{result}</p>
            </Popup>

          </form>



            </Grid>
        </div>
      );

}
 export default InputForm;
